import"./components-DAG7JxQH.js";const e={"Strategy & Planning":[{id:1,name:"Product Roadmap Generator",description:"Create comprehensive product roadmaps with strategic milestones, feature prioritization, and timeline planning. This template helps you map out your product development journey with clear objectives and measurable outcomes.",promptTemplate:"Generate a comprehensive product roadmap for \"${project.name}\" - ${project.oneSentencePitch || 'our product'}.\n\nThe project addresses this problem: ${project.problemStatement || 'Various user needs and market opportunities'}\n\nTarget audience: ${project.targetAudience || 'General users'}\n\nUnique solution approach: ${project.uniqueSolution || 'Innovative features and user experience'}\n\nPlease generate a 6-month product roadmap that includes:\n1. Key milestones and deliverables\n2. Feature prioritization matrix\n3. Timeline with dependencies\n4. Success metrics and KPIs\n5. Risk assessment and mitigation strategies"},{id:2,name:"Market Research Assistant",description:"Conduct thorough market analysis using AI to identify trends, competitor landscapes, and customer insights. This template helps you gather and synthesize market data to make informed strategic decisions.",promptTemplate:`For the project "${project.name}" - ${project.oneSentencePitch||"a new venture"}, conduct a comprehensive market research analysis.\n\nProblem being solved: ${project.problemStatement||"Market needs analysis required"}\n\nTarget market: ${project.targetAudience||"Market segments to be identified"}\n\nCompetitive advantage: ${project.uniqueSolution||"Differentiation factors to be analyzed"}\n\nPlease provide:\n1. Market size and growth potential analysis\n2. Competitive landscape mapping\n3. Target customer segment analysis\n4. Market trends and opportunities\n5. Pricing strategy recommendations\n6. Go-to-market strategy insights`},{id:3,name:"User Persona Creator",description:"Build detailed user personas based on data analysis and behavioral patterns. This AI template helps create accurate representations of your target audience for better product development.",promptTemplate:`Create detailed user personas for "${project.name}" - ${project.oneSentencePitch||"our project"}.\n\nTarget audience: ${project.targetAudience||"Users to be defined"}\n\nProblem we solve: ${project.problemStatement||"User pain points to be identified"}\n\nOur unique approach: ${project.uniqueSolution||"Solution differentiation"}\n\nGenerate 3-4 detailed user personas including:\n1. Demographics and psychographics\n2. Goals, motivations, and pain points\n3. Behavioral patterns and preferences\n4. Technology adoption and usage patterns\n5. Decision-making processes\n6. Key messaging that resonates with each persona`}],"Marketing & Sales":[{id:4,name:"Campaign Performance Optimizer",description:"Optimize your marketing campaigns with AI-driven insights and recommendations. Analyze performance data, identify improvement opportunities, and get actionable suggestions for better ROI.",promptTemplate:`Optimize marketing campaigns for "${project.name}" - ${project.oneSentencePitch||"our offering"}.\n\nTarget audience: ${project.targetAudience||"Marketing targets to be defined"}\n\nValue proposition: ${project.uniqueSolution||"Key benefits to highlight"}\n\nMarket challenge: ${project.problemStatement||"Market education needs"}\n\nCreate a campaign optimization strategy including:\n1. Channel-specific messaging and creative recommendations\n2. Audience segmentation and targeting strategies\n3. Performance metrics and KPI tracking framework\n4. A/B testing recommendations\n5. Budget allocation optimization\n6. Conversion funnel improvement tactics`},{id:5,name:"Content Strategy Planner",description:"Create compelling content strategies with AI assistance. Generate content calendars, topic suggestions, and performance predictions to maximize engagement and reach.",promptTemplate:`Develop a comprehensive content strategy for "${project.name}" - ${project.oneSentencePitch||"our brand"}.\n\nAudience: ${project.targetAudience||"Content audience to be defined"}\n\nCore message: ${project.uniqueSolution||"Brand differentiation to communicate"}\n\nProblem focus: ${project.problemStatement||"Issues to address through content"}\n\nGenerate:\n1. 3-month content calendar with themes and topics\n2. Content pillar strategy aligned with brand values\n3. Platform-specific content recommendations\n4. SEO keyword strategy and content optimization\n5. Engagement tactics and community building approach\n6. Content performance measurement framework`},{id:6,name:"Sales Funnel Analyzer",description:"Optimize your sales process with AI-powered funnel analysis. Identify bottlenecks, predict conversion rates, and get recommendations for improving sales performance.",promptTemplate:`Analyze and optimize the sales funnel for "${project.name}" - ${project.oneSentencePitch||"our offering"}.\n\nTarget customers: ${project.targetAudience||"Customer segments to be analyzed"}\n\nValue proposition: ${project.uniqueSolution||"Unique selling points"}\n\nCustomer problem: ${project.problemStatement||"Pain points driving purchase decisions"}\n\nProvide:\n1. Complete sales funnel mapping from awareness to conversion\n2. Conversion rate optimization recommendations\n3. Lead qualification and scoring framework\n4. Sales enablement materials and messaging\n5. Customer objection handling strategies\n6. Retention and upselling opportunities analysis`}],Technical:[{id:7,name:"Code Review Assistant",description:"Get AI-powered code review suggestions, best practices recommendations, and quality improvements. Enhance your development workflow with automated code analysis and optimization tips.",promptTemplate:`Provide a comprehensive code review and quality framework for "${project.name}" - ${project.oneSentencePitch||"our technical project"}.\n\nTechnical challenge: ${project.problemStatement||"Development challenges to address"}\n\nUser base: ${project.targetAudience||"End users and their technical needs"}\n\nTechnical advantage: ${project.uniqueSolution||"Technology differentiation"}\n\nDevelop:\n1. Code review checklist and standards\n2. Automated testing strategy and coverage requirements\n3. Performance monitoring and optimization guidelines\n4. Security audit procedures and compliance requirements\n5. Documentation standards and API guidelines\n6. Deployment and CI/CD pipeline recommendations`},{id:8,name:"Performance Monitor",description:"Monitor application performance with AI-driven anomaly detection. Get real-time insights, predictive alerts, and optimization recommendations for your technical infrastructure.",promptTemplate:`Design a comprehensive performance monitoring strategy for "${project.name}" - ${project.oneSentencePitch||"our application"}.\n\nSystem requirements driven by: ${project.problemStatement||"Performance challenges to solve"}\n\nUser expectations: ${project.targetAudience||"User base performance needs"}\n\nTechnical edge: ${project.uniqueSolution||"Performance advantages to maintain"}\n\nCreate:\n1. Performance metrics and KPI framework\n2. Real-time monitoring and alerting system design\n3. Load testing and capacity planning strategy\n4. Database and API optimization recommendations\n5. User experience performance tracking\n6. Incident response and performance recovery procedures`},{id:9,name:"API Documentation Generator",description:"Automatically generate comprehensive API documentation with AI assistance. Create clear, well-structured docs that improve developer experience and reduce support overhead.",promptTemplate:`Generate comprehensive API documentation for "${project.name}" - ${project.oneSentencePitch||"our platform"}.\n\nDeveloper needs based on: ${project.problemStatement||"Integration challenges to solve"}\n\nTarget developers: ${project.targetAudience||"Developer community and their needs"}\n\nAPI advantages: ${project.uniqueSolution||"Unique API features and benefits"}\n\nProduce:\n1. Complete API reference documentation with examples\n2. Getting started guide and authentication setup\n3. SDK and integration tutorials for popular frameworks\n4. Error handling and troubleshooting guide\n5. Rate limiting and best practices documentation\n6. Interactive API explorer and testing interface design`}]},n=()=>{try{const e=localStorage.getItem("spatium_custom_templates");if(e)return JSON.parse(e)}catch(e){}return[]},t=e=>{const t=n(),a={...e,id:e.id||`custom_${Date.now()}`,isCustom:!0,createdAt:e.createdAt||(new Date).toISOString()};return(e=>{try{localStorage.setItem("spatium_custom_templates",JSON.stringify(e))}catch(n){}})([...t,a]),a};export{t as a,n as l,e as t};
